function show_data(ext) {
    ext = JSON.parse(ext);
    $('#data-id').val(ext.id);
    $('#size').val(ext.size);
    $('#type').val(ext.type);
    $('#expiration-date').val(Date.parse(ext.expiration_date));
    $('#location').val(ext.location);
    $('#place').val(ext.place);
    $('#floor').val(ext.floor);
    $('#observation').val(ext.observation);
}
